describe("angular easypiechart directive",function(){var e,t;beforeEach(module("easypiechart")),beforeEach(inject(function(i,n){t=n,e=i})),it("should have percentage default value 0",function(i){t.percent=null;var n=angular.element('<div easypiechart percent="percent" options="options"></div>');e(n)(t),t.$digest(),expect(n.isolateScope().percent).toBe(0)}),it("inserts the element with a canvas element",function(){t.percent=-45,t.options={};var i=angular.element('<div easypiechart percent="percent" options="options"></div>');e(i)(t),t.$digest(),expect(i.html()).toContain("canvas")}),it("gets the options right",function(i){t.percent=0,t.options={animate:{duration:0,enabled:!1},barColor:"#2C3E50",scaleColor:!1,lineWidth:20,lineCap:"circle"};var n=angular.element('<div easypiechart percent="percent" options="options"></div>');e(n)(t),t.$digest(),expect(n.isolateScope().options.animate.duration).toBe(0),expect(n.isolateScope().options.lineCap).toBe("circle")}),it("has its own default options",function(i){t.percent=0,t.options={};var n=angular.element('<div easypiechart percent="percent" options="options"></div>');e(n)(t),t.$digest(),expect(n.isolateScope().options.size).toBe(110),expect(n.isolateScope().options.animate.enabled).toBe(!0)}),it("takes size option the right way",function(){t.percent=0,t.options={size:200};var i=angular.element('<div easypiechart percent="percent" options="options"></div>');e(i)(t),t.$digest(),expect(i.html()).toContain('height="200"'),expect(i.html()).toContain('width="200"')})});